DROP TABLE IF EXISTS `t_discuss`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `t_discuss` (
  `id` varchar(64) NOT NULL,
  `discuss_content` text COMMENT '讨论的内容',
  `topic_id` varchar(64) DEFAULT NULL,
  `create_time` datetime NOT NULL,
  `user_id` varchar(64) DEFAULT NULL,
  `praises` int(11) DEFAULT NULL COMMENT '点赞',
  `down` int(11) DEFAULT NULL COMMENT '踩',
  `discuss_pictures` varchar(1000) DEFAULT NULL COMMENT '配图，可以是多张',
  PRIMARY KEY (`id`),
  UNIQUE KEY `t_discuss_id_uindex` (`id`),
  KEY `t_discuss_t_topic_id_fk` (`topic_id`),
  KEY `t_discuss_t_user_id_fk` (`user_id`),
  CONSTRAINT `t_discuss_t_topic_id_fk` FOREIGN KEY (`topic_id`) REFERENCES `t_topic` (`id`) ON DELETE SET NULL,
  CONSTRAINT `t_discuss_t_user_id_fk` FOREIGN KEY (`user_id`) REFERENCES `t_user` (`id`) ON DELETE SET NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='讨论';
/*!40101 SET character_set_client = @saved_cs_client */;

