DROP TABLE IF EXISTS `t_options`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `t_options` (
  `id` varchar(64) NOT NULL,
  `apply_user_id` varchar(64) DEFAULT NULL COMMENT '申请人的id',
  `handle_user_id` varchar(64) DEFAULT NULL COMMENT '处理人的id',
  `apply_reason` text COMMENT '申请的理由',
  `apply_type` varchar(200) DEFAULT NULL COMMENT '申请的类型，贴子置顶，版主，区主，显示自己的分区',
  `apply_time` datetime NOT NULL COMMENT '申请时间',
  `handle_time` varchar(64) DEFAULT NULL COMMENT '处理时间',
  `post_id` varchar(64) DEFAULT NULL COMMENT '贴子表',
  `area_id` varchar(64) DEFAULT NULL COMMENT '分区id',
  `board_id` varchar(64) DEFAULT NULL COMMENT '板块表',
  `remakes` text COMMENT '备注',
  `status` int(2) DEFAULT NULL COMMENT '状态，撤销,待审核，审核通过，审核拒绝，超时，忽略(时间大于7天，状态变成超时)',
  `brief` text COMMENT '简单介绍',
  PRIMARY KEY (`id`),
  UNIQUE KEY `t_apply_id_uindex` (`id`),
  KEY `t_apply_t_user_id_fk` (`apply_user_id`),
  KEY `t_apply_t_area_id_fk` (`area_id`),
  KEY `t_apply_t_board_id_fk` (`board_id`),
  KEY `t_apply_t_post_id_fk` (`post_id`),
  KEY `t_options_t_user_id_fk` (`handle_user_id`),
  CONSTRAINT `t_apply_t_area_id_fk` FOREIGN KEY (`area_id`) REFERENCES `t_area` (`id`) ON DELETE SET NULL,
  CONSTRAINT `t_apply_t_board_id_fk` FOREIGN KEY (`board_id`) REFERENCES `t_board` (`id`) ON DELETE SET NULL,
  CONSTRAINT `t_apply_t_post_id_fk` FOREIGN KEY (`post_id`) REFERENCES `t_post` (`id`) ON DELETE SET NULL,
  CONSTRAINT `t_apply_t_user_id_fk` FOREIGN KEY (`apply_user_id`) REFERENCES `t_user` (`id`) ON DELETE SET NULL,
  CONSTRAINT `t_options_t_user_id_fk` FOREIGN KEY (`handle_user_id`) REFERENCES `t_user` (`id`) ON DELETE SET NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='申请审核的操作表';
/*!40101 SET character_set_client = @saved_cs_client */;

