DROP TABLE IF EXISTS `t_topic`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `t_topic` (
  `id` varchar(64) NOT NULL,
  `topic_title` varchar(64) NOT NULL COMMENT '话题的标题',
  `user_id` varchar(64) DEFAULT NULL COMMENT '话题表',
  `topic_picture` varchar(250) NOT NULL COMMENT '话题的配图',
  `topic_describe` text COMMENT '话题的详细描述',
  `create_time` datetime NOT NULL COMMENT '创建时间',
  `level` int(2) DEFAULT NULL COMMENT '话题的等级',
  `topic_participations` int(11) DEFAULT '0' COMMENT '参与度',
  `topic_keyword` varchar(255) DEFAULT NULL COMMENT '关键词（2-6）',
  `topic_seen` int(11) DEFAULT NULL COMMENT '浏览量',
  PRIMARY KEY (`id`),
  UNIQUE KEY `t_topic_id_uindex` (`id`),
  KEY `t_topic_t_user_id_fk` (`user_id`),
  CONSTRAINT `t_topic_t_user_id_fk` FOREIGN KEY (`user_id`) REFERENCES `t_user` (`id`) ON DELETE SET NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='话题表';
