mapper.update:
update test_bbs.t_post
user_id=#{userId}, post_type=#{postType}, post_title=#{postTitle}, post_content=#{postContent}, create_time=#{createTime}, board_id=#{boardId}, area_id=#{areaId}, participants=#{participants}, praises=#{praises}, comments=#{comm}, ban_comment=#{banComment}, up=#{up}, secret=#{secret}, first_image=#{firstImage}
where id=#{id}

mapper.select:
select
from test_bbs.t_post p
where p.area_id=#{id}

select post.post_title,post.create_time, type.name post_type
    from test_bbs.t_post post,
         test_bbs.post_type type
where post.post_type = type.id;
